import React, { Component } from 'react';
import Aux from '../../../hoc/Aux/Aux';
import Match from '../../Match/Match';
import axios from '../../../axios-matches';
import { Route, Redirect } from 'react-router-dom';
import MatchDetail from '../../MatchDetail/MatchDetail'

class LiveMatches extends Component {

    state = {
        liveMatches: []
    }

    componentDidMount() {
        console.log(this.props)
        this.fetchLiveMatches();
    }

    fetchLiveMatches() {
        let obj = {
            "apikey": "tTzabHsmnIPgA6CNdPwKGRCpduW2"
        }
        axios.post('', obj)
            .then(res => {
                console.log(res);
                this.setState({ liveMatches: res ? res.data ? res.data.matches : [] : [] })
            })
            .catch(err => console.log(err));
    }

    fetchMatchDetail() {
        this.props.history.push('/match-detail')
    }

    render() {
        let match = 'No matches available';
        if (this.state.liveMatches.length > 1) {
            const matchData = this.state.liveMatches;
            match = matchData.map((match) => {
                return (
                    <div>
                        <Match
                            key={match.unique_id}
                            date={match.date}
                            dateTimeGmt={match.dateTimeGmt}
                            matchStarted={match.matchStarted}
                            teamOne={match['team-1']}
                            teamTwo={match['team-2']}
                            toss={match['toss_winner_team']}
                            type={match.type}
                            uniqueId={match.unique_id}
                            clicked={() => this.fetchMatchDetail(match.uniqueId)}
                        />
                        <Route
                            path={this.props.match.path + '/match-detail'}
                            component={ContactData} />
                    </div>)
            })
        }
        return (
            <Aux>
                {match}
            </Aux>
        )
    }
}

export default LiveMatches;
