import { FETCH_MATCHES, FETCH_MATCHES_FAILED } from '../actions/actionTypes';
import { updateObject } from '../utility';

const initialState = {
    data: {},
    error: false
};

const fetchMatches = (state, action) => {
    return updateObject(state, {
        data: action.data,
        error: false
    });
};

const fetchMatchesFailed = (state, action) => {
    return updateObject(state, { error: true });
};

const reducer = (state = initialState, action) => {
    if (action === FETCH_MATCHES) {
         return fetchMatches(state, action);
    }
    if (action === FETCH_MATCHES_FAILED) {
        return fetchMatchesFailed(state, action);
    }
    return state;
}

export default reducer;
