import React from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';


const match = (props) => {
    return (
        <div className="container">
            <div className="row" >
                <div className="col-xs-1 col-lg-2" >
                </div>
                <div className="col-xs-10 col-lg-8" style={{ background: 'grey', marginTop: 20 }}>
                <div style={{background : 'lightblue' , paddingTop:10, paddingBottom:10 ,paddingLeft:20,border:2 ,marginTop: 10}}>
                      {props.name || props.title ? <h2>{props.name || props.title}</h2> :<h2>{props.teamOne} vs {props.teamTwo} {props.type}</h2>}
                    { props.title ? <h3>{props.description} </h3>:<h3>Toss : {props.toss}</h3>}
                        <h6>{props.date}</h6>
                        <button 
                        disabled={props.name ? true : false}
                        className="btn btn-primary" 
                        onClick={props.clicked}>match details</button>
                    </div>
                </div>
                <div className="col-xs-1 col-lg-2" >
                </div>
            </div>
        </div>
    );
}

export default match;
