import React, { Component } from 'react';
import axios from '../../axios-matches';


class MatchDetail extends Component {

    state = {
        matchDetail: []
    }

    componentDidMount() {
        console.log("hello");
        console.log(this.props);
        this.fetchMatchDetail();
    }

    fetchMatchDetail() {
        let uniqueId = this.props.match.params.id
        let obj = {
            "apikey": "tTzabHsmnIPgA6CNdPwKGRCpduW2",
            "unique_id": uniqueId
        }
        axios.post('/cricketScore/', obj)
            .then(res => {
                console.log(res);
                this.setState({ matchDetail: res ? res.data ? res.data.data : [] : [] });
                setTimeout(()=>{
                    console.log(this.state.matchDetail);
                }, 2000) 
            })
            .catch(err => console.log(err));
    }

    render() {
        let matchDetail = "No match detail available";
        if (this.state.matchDetail && this.state.matchDetail.length > 1) {
            const matchData = this.state.matchDetail;
            matchDetail = matchData.map((match, index) => {
                return (
                    <div className="container">
                        <div className="row" >
                            <div className="col-xs-2 col-lg-2"></div>
                            <div className="col-xs-8 col-lg-8" style={{ background : 'grey'}}>
                            <div style={{background : 'lightblue'}}>
                                <h3>{match['team-1']} VS {match['team-2']} </h3>
                                <h4>{match.score}</h4>
                                <h5>{match.state}</h5>
                            </div>
                            </div>
                            <div className="col-xs-2 col-lg-2"></div>
                        </div>

                    </div>
                );
            })
        }
        return (
            <div>
                {matchDetail}
            </div>
        )

    }
}

export default MatchDetail;
