import React, { Component } from 'react';
import Aux from '../../../hoc/Aux/Aux';
import Match from '../../Match/Match';
import axios from '../../../axios-matches';

class ScheduledMatches extends Component {

    constuctor() {
        this.fetchMatchDetail = this.fetchMatchDetail.bind(this);
      }

    state = {
        matchCalendar: []
    }

    componentDidMount() {
        console.log(this.props)
        this.fetchMatchesCalander();
    }

    fetchMatchesCalander() {
        let obj = {
            "apikey": "tTzabHsmnIPgA6CNdPwKGRCpduW2"
        }
        axios.post('matchCalendar/', obj)
            .then(res => {
                this.setState({ matchCalendar: res ? res.data ? res.data.data : []:[] })
            })
            .catch(err => console.log(err));
    }

    fetchMatchDetail = () => {
        console.log('inside fetch')
        this.props.history.push('/match-detail')
    }

    render() {
        let match = 'No matches available';
        let matchData; 
        // = this.state.matchCalendar['0...99']
        if (this.state.matchCalendar.length > 1) {
             matchData = this.state.matchCalendar;
            match = matchData.map((match) => {
                return (
                    <div  key={match.unique_id}>
                        <Match
                            date={match.date}
                            name={match.name}
                            dateTimeGmt=''
                            matchStarted='false'
                            teamOne=''
                            teamTwo=''
                            toss='No Toss'
                            type=''
                            uniqueId={match.unique_id}
                            clicked={this.fetchMatchDetail}
                        />
                    </div>)
            })
        }
        return (
            <Aux>
                {match}
            </Aux>
        )
    }
}

export default ScheduledMatches;
