import React, { Component } from 'react';
import Aux from '../../../hoc/Aux/Aux';
import Match from '../../Match/Match';
import axios from '../../../axios-matches';

class UpcomingMatches extends Component {

    state = {
        matchCalendar: [],
        upcomingArray : [],
    }

    componentDidMount() {
        this.fetchMatchesCalander();
    }

    fetchMatchesCalander() {
        let obj = {
            "apikey": "tTzabHsmnIPgA6CNdPwKGRCpduW2"
        }
        axios.post('matchCalendar/', obj)
            .then(res => {
                this.setState({ matchCalendar: res ? res.data ? res.data.data : []:[] });
                this.mapResponse()
            })
            .catch(err => console.log(err));
    }

    mapResponse =()=>{
        const response = this.state.matchCalendar ;
        let upcomingArray = response.splice(0, 10);
        this.setState({upcomingArray : upcomingArray});
    }

    render() {
        let match = 'No matches available';
        let matchData; 
        // = this.state.matchCalendar['0...99']
        if (this.state.upcomingArray.length > 1) {
             matchData = this.state.upcomingArray;
            match = matchData.map((match, index) => {
                return (
                    <div  key={index}>
                        <Match
                            date={match.date}
                            name={match.name}
                            dateTimeGmt=''
                            matchStarted='false'
                            teamOne=''
                            teamTwo=''
                            toss='No Toss'
                            type=''
                            uniqueId={match.unique_id}
                            clicked={this.fetchMatchDetail}
                        />
                    </div>)
            })
        }
        return (
            <Aux>
                {match}
            </Aux>
        )
    }
}

export default UpcomingMatches;
