import React, { Component } from 'react';
import axios from '../../../axios-matches';

class PlayerStats extends Component {

    state = {
        playerStats: {}
    }

    componentDidMount() {
        localStorage.clear();
        this.fetchPlayerStatics();
    }

    fetchPlayerStatics = () => {
        const id = this.props.match.params.pid
        const cachedData = localStorage.getItem(id);

        if (cachedData) {
            this.setState({ playerStats: JSON.parse(cachedData) });
            console.log(this.state.playerStats);
        } else {

            let obj = {
                "apikey": "tTzabHsmnIPgA6CNdPwKGRCpduW2",
                "pid": id
            }
            console.log(obj);
            axios.post('/playerStats/', obj)
                .then(res => {
                    console.log("api call");
                    console.log(res);
                    let data = res ? res.data : {};
                    console.log(data);
                    this.onSetResult(data, id)
                })
                .catch((err) => console.log(err));
        }
    }

    onSetResult = (data, key) => {
        localStorage.setItem(key, JSON.stringify(data));

        this.setState({ playerStats: data });
    };

    render() {
        const pData = this.state.playerStats;
        return (
            <div className="container">
                <div className="row" >
                    <div className="col-xs-2 col-lg-2"></div>
                    <div className="col-xs-8 col-lg-8" style={{ background: 'grey' }}>
                        <div style={{ background: 'lightblue', paddingTop: 10, paddingLeft: 20,paddingBottom: 10, marginBottom:10, border: 2, marginTop: 10 }}>
                           <div style={{ background: 'lightgrey', paddingTop: 10, paddingLeft: 20,paddingRight:20, paddingBottom: 10, marginBottom:10, border: 2, marginTop: 10 }}>
                            <img src={pData.imageUrl} alt="Profile not availble"/>
                           </div>
                            <h5>Name : {pData.name}</h5>
                            <h6>FullName : {pData.fullName}</h6>
                            <h6>Country : {pData.country}</h6>
                            <p><span>Date-of-Birth :</span>{pData.born}</p>
                            <p><span>Current-Age :</span>{pData.currentAge}</p>
                            <p><span>Batting-Style :</span>{pData.battingStyle}</p>
                            <p><span>Bowling-Style :</span>{pData.bowlingStyle}</p>
                            <p><span>Major-Teams :</span>{pData.majorTeams}</p>
                        </div>
                    </div>
                    <div className="col-xs-2 col-lg-2"></div>
                </div>
            </div>
        )
    }
}

export default PlayerStats;
