import React, { Component } from 'react';
import axios from '../../axios-matches';

class Search extends Component {

    state = {
        query: '',
        playersList: []
    };

    onSearch = event => {
        event.preventDefault();

        const { query } = this.state;
        console.log(query);
        if (query === '') {
            return;
        }

        const cachedData = localStorage.getItem(query);

        if (cachedData) {
            this.setState({ playersList: JSON.parse(cachedData) });
            console.log(this.state.playersList);
        } else {
            let obj = {
                "apikey": "tTzabHsmnIPgA6CNdPwKGRCpduW2",
                "name": query
            }
            axios.post('/playerFinder/', obj)
                .then(res => {
                    console.log("api call");
                    console.log(res);
                    let data = res ? res.data ? res.data.data : [] : [];
                    console.log(data);
                    this.onSetResult(data, query)
                })
                .catch((err) => console.log(err));
        }
    };

    onSetResult = (data, key) => {
        localStorage.setItem(key, JSON.stringify(data));

        this.setState({ playersList: data });
    };

    onChange = event => {
        this.setState({ query: event.target.value });
    };



    render() {
        return (
            <div className="container">
                <div className="coloumn">
                    <div className="row">
                        <div className="col-xs-4 col-lg-4"></div>
                        <div className="col-xs-6 col-lg-6" style={{ paddingLeft: 70 }}>
                            <form onSubmit={this.onSearch}>
                                <input type="text" onChange={this.onChange} />
                                <button type="btn btn-primary">Search</button>
                            </form>
                        </div>
                        <div className="col-xs-3 col-lg-3"></div>
                    </div>
                    {this.state.playersList ? this.state.playersList.map((value,index) => {
                        return (<div key={index} className="row" style={{ marginTop: 20 }} >
                            <div className="col-xs-2 col-lg-2"></div>
                            <div className="col-xs-8 col-lg-8" style={{ background: 'grey' }}>
                                <div style={{ background: 'lightblue', paddingTop: 10, paddingLeft: 20, border: 2, marginTop: 10,marginBottom : 10 }}>
                                    <h4> Name : {value.fullName}</h4>
                                    <h5>NickName : {value.name} </h5>
                                    <button
                                        className="btn btn-info"
                                        >View Stats</button>
                                </div>
                            </div>
                            <div className="col-xs-2 col-lg-2"></div>
                        </div>
                        );
                    }) : null}
                </div>

            </div>
        );
    }
}

export default Search;
