import React, { Component } from 'react';
import axios from '../../axios-matches';

class Search extends Component {

    state = {
        query: '',
        playersList: []
    };

    onSearch = event => {
        event.preventDefault();

        const { query } = this.state;
        console.log(query);
        if (query === '') {
            return;
        }

        const cachedData = localStorage.getItem(query);

        if (cachedData) {
            this.setState({ playersList: cachedData });
            console.log(this.state.playersList);
        } else {
            let obj = {
                "apikey" : "tTzabHsmnIPgA6CNdPwKGRCpduW2",
                "name" : query
            }
            axios.post('/playerFinder/' , obj)
                .then(res => {
                    console.log("api call");
                    console.log(res);
                    let data = res ? res.data ? res.data.data : [] : [] ;
                    this.onSetResult(data, query)
                })
                .catch((err)=>console.log(err));
        }
    };

    onSetResult = (data, key) => {
        localStorage.setItem(key, data);

        this.setState({ playersList: data });
    };

    onChange = event => {
        this.setState({ query: event.target.value });
    };



    render() {
        return (
            <div className="container">
                <form onSubmit={this.onSearch}>
                    <input type="text" onChange={this.onChange} />
                    <button type="btn btn-primary">Search</button>
                </form>
            </div>
        );
    }
}

export default Search;
